sys_variable_value.value:
(function execute(inputs, outputs) {
  //outputs.password = new GlideEncrypter().decrypt(inputs.password.getDecryptedValue());
  //var pass = inputs.password;
  //outputs.password = pass.getDecryptedValue();
  var tablename = 'ssh_credentials'
  var CI = new GlideRecord(tablename);  
  CI.addQuery('sys_id', '498d34c6dbb37300df2da455ca9619d6'); 
  CI.query(); 
  CI.next(); 

  var password = CI.password;
  var decrypted = password.getDecryptedValue(); 
  gs.info(decrypted);
  outputs.password = decrypted;
  outputs.variable = CI.name;
})(inputs, outputs);

