sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
  var num = inputs.echo0or1;
  var comment;
  if (num === "0"){
    comment = "0です";
  }else{
    comment = "1です";
  }

  outputs.variable = comment;

})(inputs, outputs);

