sys_variable_value.value:
cd ../;
sudo touch tarzipmoving.sh;
sudo chmod 777 tarzipmoving.sh;
echo '#!/bin/sh' > tarzipmoving.sh;
echo '' >> tarzipmoving.sh;
echo 'getdir = opt/vuls/owasp/project/MS-master' >> tarzipmoving.sh;
echo '' >> tarzipmoving.sh;
echo 'if [ -d $getdir ] ; then' >> tarzipmoving.sh;
echo '	tar zcvf ManufacturingSpace.tar.gz opt/vuls/owasp/project/MS-master' >> tarzipmoving.sh;
echo 'else' >> tarzipmoving.sh;
echo '	echo "this is file"' >> tarzipmoving.sh;
echo 'fi' >> tarzipmoving.sh;
sudo ./tarzipmoving.sh

expect -c "
set timeout 60
spawn scp -Cpqr sudoope@10.1.8.15:/opt/vuls/owasp/project/ManufacturingSpace.tar.gz /opt/owasp/DependencyCheck
expect {
\" Are you sure you want to continue connecting (yes/no)? \" {
send \"yes\r\"
expect \"password:\"
send \"sudoope\r\"
} \"password:\" {
send \"sudoope\r\"
}
}
interact
"

