sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
  //データ削除
  var recdelete = new GlideRecord('x_405103_owaspauto_demo_odc_vulnerabilities');
  recdelete.query(); 
  while(recdelete.next()) { 
    recdelete.deleteRecord(); }
})(inputs, outputs);

(function execute(inputs, outputs) {

  //変数宣言
  jsondata = JSON.parse(inputs.AdditionalInfomation);
  ODCdata = [];
  ODCresult = [];

  ODCdataObj = JSON.parse(jsondata.owasp_dc_result);
  for(var i=0;i<ODCdataObj.length;i++){
    ODCdataStr = "[\"" + ODCdataObj[i] + "\"]";
    ODCDataJSON = JSON.parse(ODCdataStr);
    ODCDataJSONparse = JSON.parse("[" + ODCDataJSON[0] + "]");
    ODCresult.push(ODCDataJSONparse);
  }

  gs.info(typeof ODCresult[0][0] + ":::【Vul】" + ODCresult[0][0]); 

  for(var j=0; j<ODCresult.length; j++){
    var count = 0; //合致するもの計測値
    var recd = new GlideRecord('x_405103_owaspauto_demo_odc_vulnerabilities');
    recd.query();
    while(recd.next()) {
      if(recd.name == ODCresult[j][1]){
        //合致するものがある場合はcountする
        count = count + 1;
      }
    }
    //gs.info("【ファイル名】"+ ODCresult[j][0] + "：【脆弱性名】"+  ODCresult[j][1] + " ：【カウント】"+count);
    //GlideRecordでテーブルにInsertする
    if(count === 0){
      var rec = new GlideRecord('x_405103_owaspauto_demo_odc_vulnerabilities');
      rec.initialize();
      rec.name = ODCresult[j][1];
      rec.description = "demo：" + ODCresult[j][2];
      rec.cvssv2_score = ODCresult[j][3];
      rec.cvssv2_severity = ODCresult[j][4];
      rec.cvssv2_vector = ODCresult[j][5];
      rec.cvssv3_score = ODCresult[j][6];
      rec.cvssv3_severity = ODCresult[j][7];
      rec.cvssv3_vector = ODCresult[j][8];
      rec.references = ODCresult[j][9];
      rec.vulnerable_software_versions = ODCresult[j][10];
      //rec.datatime = ODCresult[j][11];
      rec.insert();
    }
  }  

})(inputs, outputs);

