sys_element_mapping.value:
cd ../;
touch tarzipmoving.sh;
chmod 777 tarzipmoving.sh;
echo '#!/bin/sh' > tarzipmoving.sh;
echo '' >> tarzipmoving.sh;
echo 'getdir="{{action.fromdirectory}}"' >> tarzipmoving.sh;
echo '' >> tarzipmoving.sh;
echo 'if [ -d $getdir ] ; then' >> tarzipmoving.sh;
echo '	tar zcvf {{action.pjname}}.tar.gz {{action.fromdirectory}}' >> tarzipmoving.sh;
echo 'else' >> tarzipmoving.sh;
echo '	echo "this is file"' >> tarzipmoving.sh;
echo 'fi' >> tarzipmoving.sh;
./tarzipmoving.sh

sys_hub_input_scripts.script:
{"credential_alias":{"scriptActive":true,"script":"/*
**Access Flow/Action data using the fd_data object. Script must return a value.
**example: var shortDesc = fd_data.trigger.current.short_description;
**return shortDesc;
*/
return fd_data.action_inputs.credentialalias;"}}

