sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
//testcommandParam ・・・ディレクトリかどうかを実行し、ディレクトリであれば0 それ以外は1と出力
  var num = parseInt(inputs.testcommandParam, 10);
  var dir = inputs.FromDir;
  var projectname = inputs.PJname;
  var Jdir;
  gs.info(typeof num);
  //余計な空白は削除  
  dir = dir.trim();

  //dirの指定がディレクトリの場合
  if(num === 0){
    gs.info("num0");
    if(dir.slice(-1) == "/"){
      //最後が/の場合は2回実行して最後の階層を抜く
      Jdir = dir.substr(0,dir.lastIndexOf("/"));
      Jdir = Jdir.substr(0,Jdir.lastIndexOf("/")); 
    }else{
      //最後が/ではない場合は、最後の階層を抜く
      Jdir = dir.substr(0,dir.lastIndexOf("/")); 
　　}
    //ディレクトリの場合は語尾にManufacturingSpace.tar.gzとつける
    Jdir = Jdir +"/"+projectname+".tar.gz";
  }else{
    gs.info("num1");
    //dirの指定がファイルの場合(そのまま）
    Jdir = dir;
  }

  outputs.fromdirectory_judged = Jdir;
})(inputs, outputs);

